import React, { useState } from 'react';
import { Plus, X, ChevronDown, ChevronUp } from 'lucide-react';
import { getAvailableAgents } from '../../api/projectsApi';
import './TeamBuilder.css';

const TeamBuilder = ({ teams, onAddTeam, onUpdateTeam, onDeleteTeam, onReorderTeams }) => {
  const [showAddForm, setShowAddForm] = useState(false);
  const [expandedTeam, setExpandedTeam] = useState(null);
  const [newTeam, setNewTeam] = useState({
    name: '',
    description: '',
    icon: '游논',
    color: '#4A90E2',
    agents: [],
    checkpointEnabled: true
  });

  const availableAgents = getAvailableAgents();

  const handleAddTeam = () => {
    if (!newTeam.name.trim()) {
      alert('Please enter a team name');
      return;
    }

    if (newTeam.agents.length === 0) {
      alert('Please select at least one agent');
      return;
    }

    onAddTeam(newTeam);
    setNewTeam({
      name: '',
      description: '',
      icon: '游논',
      color: '#4A90E2',
      agents: [],
      checkpointEnabled: true
    });
    setShowAddForm(false);
  };

  const handleAgentToggle = (agentId, isNewTeam = true) => {
    if (isNewTeam) {
      const agents = newTeam.agents.includes(agentId)
        ? newTeam.agents.filter(id => id !== agentId)
        : [...newTeam.agents, agentId];
      setNewTeam({ ...newTeam, agents });
    }
  };

  const handleTeamAgentToggle = (teamId, agentId) => {
    const team = teams.find(t => t.id === teamId);
    const agents = team.agents.includes(agentId)
      ? team.agents.filter(id => id !== agentId)
      : [...team.agents, agentId];
    onUpdateTeam(teamId, { agents });
  };

  const toggleTeamExpansion = (teamId) => {
    setExpandedTeam(expandedTeam === teamId ? null : teamId);
  };

  const moveTeam = (teamId, direction) => {
    const currentIndex = teams.findIndex(t => t.id === teamId);
    if (direction === 'up' && currentIndex > 0) {
      const newOrder = [...teams];
      [newOrder[currentIndex], newOrder[currentIndex - 1]] = [newOrder[currentIndex - 1], newOrder[currentIndex]];
      onReorderTeams(newOrder.map(t => t.id));
    } else if (direction === 'down' && currentIndex < teams.length - 1) {
      const newOrder = [...teams];
      [newOrder[currentIndex], newOrder[currentIndex + 1]] = [newOrder[currentIndex + 1], newOrder[currentIndex]];
      onReorderTeams(newOrder.map(t => t.id));
    }
  };

  return (
    <div className="team-builder">
      <div className="team-builder-header">
        <h3>Agent Teams</h3>
        <button
          className="add-team-btn"
          onClick={() => setShowAddForm(!showAddForm)}
        >
          <Plus size={16} />
          Add Team
        </button>
      </div>

      {showAddForm && (
        <div className="add-team-form">
          <div className="form-header">
            <h4>Create New Team</h4>
            <button className="close-btn" onClick={() => setShowAddForm(false)}>
              <X size={18} />
            </button>
          </div>

          <div className="form-group">
            <label>Team Name *</label>
            <input
              type="text"
              value={newTeam.name}
              onChange={(e) => setNewTeam({ ...newTeam, name: e.target.value })}
              placeholder="e.g., Backend Squad, Frontend Team"
            />
          </div>

          <div className="form-group">
            <label>Description</label>
            <textarea
              value={newTeam.description}
              onChange={(e) => setNewTeam({ ...newTeam, description: e.target.value })}
              placeholder="What does this team do?"
              rows={2}
            />
          </div>

          <div className="form-row">
            <div className="form-group">
              <label>Icon</label>
              <input
                type="text"
                value={newTeam.icon}
                onChange={(e) => setNewTeam({ ...newTeam, icon: e.target.value })}
                placeholder="游논"
                maxLength={2}
              />
            </div>
            <div className="form-group">
              <label>Color</label>
              <input
                type="color"
                value={newTeam.color}
                onChange={(e) => setNewTeam({ ...newTeam, color: e.target.value })}
              />
            </div>
          </div>

          <div className="form-group">
            <label>
              <input
                type="checkbox"
                checked={newTeam.checkpointEnabled}
                onChange={(e) => setNewTeam({ ...newTeam, checkpointEnabled: e.target.checked })}
              />
              Enable checkpoint (require approval after this team completes)
            </label>
          </div>

          <div className="form-group">
            <label>Select Agents * ({newTeam.agents.length} selected)</label>
            <div className="agent-grid">
              {availableAgents.map(agent => (
                <div
                  key={agent.id}
                  className={`agent-option ${newTeam.agents.includes(agent.id) ? 'selected' : ''}`}
                  onClick={() => handleAgentToggle(agent.id)}
                >
                  <span className="agent-icon">{agent.icon}</span>
                  <span className="agent-label">{agent.label}</span>
                </div>
              ))}
            </div>
          </div>

          <div className="form-actions">
            <button className="cancel-btn" onClick={() => setShowAddForm(false)}>
              Cancel
            </button>
            <button className="create-btn" onClick={handleAddTeam}>
              Create Team
            </button>
          </div>
        </div>
      )}

      {teams.length === 0 && !showAddForm && (
        <div className="empty-state">
          <p>No teams yet. Click "Add Team" to get started.</p>
        </div>
      )}

      <div className="teams-list">
        {teams
          .sort((a, b) => a.executionOrder - b.executionOrder)
          .map((team, index) => (
            <div key={team.id} className="team-card">
              <div className="team-header" onClick={() => toggleTeamExpansion(team.id)}>
                <div className="team-info">
                  <span className="team-icon" style={{ color: team.color }}>
                    {team.icon}
                  </span>
                  <div className="team-details">
                    <h4>{team.name}</h4>
                    <span className="team-agent-count">
                      {team.agents.length} {team.agents.length === 1 ? 'agent' : 'agents'}
                    </span>
                  </div>
                  <span className="execution-order-badge">#{index + 1}</span>
                </div>
                <div className="team-controls" onClick={(e) => e.stopPropagation()}>
                  <button
                    className="reorder-btn"
                    onClick={() => moveTeam(team.id, 'up')}
                    disabled={index === 0}
                    title="Move up"
                  >
                    <ChevronUp size={16} />
                  </button>
                  <button
                    className="reorder-btn"
                    onClick={() => moveTeam(team.id, 'down')}
                    disabled={index === teams.length - 1}
                    title="Move down"
                  >
                    <ChevronDown size={16} />
                  </button>
                  <button
                    className="delete-team-btn"
                    onClick={() => onDeleteTeam(team.id)}
                    title="Delete team"
                  >
                    <X size={16} />
                  </button>
                </div>
              </div>

              {expandedTeam === team.id && (
                <div className="team-details-expanded">
                  {team.description && (
                    <p className="team-description">{team.description}</p>
                  )}

                  <div className="team-agents">
                    <h5>Agents in this team:</h5>
                    <div className="agent-grid">
                      {availableAgents.map(agent => {
                        const isSelected = team.agents.includes(agent.id);
                        return (
                          <div
                            key={agent.id}
                            className={`agent-option ${isSelected ? 'selected' : ''}`}
                            onClick={() => handleTeamAgentToggle(team.id, agent.id)}
                          >
                            <span className="agent-icon">{agent.icon}</span>
                            <span className="agent-label">{agent.label}</span>
                          </div>
                        );
                      })}
                    </div>
                  </div>

                  <div className="team-settings">
                    <label>
                      <input
                        type="checkbox"
                        checked={team.checkpointEnabled}
                        onChange={(e) => onUpdateTeam(team.id, { checkpointEnabled: e.target.checked })}
                      />
                      Require approval after this team completes
                    </label>
                  </div>
                </div>
              )}
            </div>
          ))}
      </div>
    </div>
  );
};

export default TeamBuilder;
